import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import RideForm from './components/RideForm';
import RideList from './components/RideList';
import './App.css';

const App = () => {
  const [recentRides, setRecentRides] = useState([]);

  useEffect(() => {
    setRecentRides(fetchedRides);
    // fetchRecentRequests();
  }, []);

  const fetchRecentRequests = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests');
      const data = await response.json();
      setRecentRequests(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching recent requests:', error);
      setRecentRequests([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, phone, destination, date, notes }),
      });
      if (response.ok) {
        alert('Ride request submitted successfully!');
        setName('');
        setPhone('');
        setDestination('airport');
        setDate('');
        setNotes('');
        fetchRecentRequests();
      } else {
        const errorData = await response.json();
        alert(`Failed to submit ride request: ${errorData.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error submitting ride request:', error);
      alert(`An error occurred: ${error.message}`);
    }
    console.log('Submitting ride request:', formData);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const queryParams = new URLSearchParams({
        destination: searchDestination,
        date: searchDate,
      }).toString();
      const response = await fetch(`http://localhost:3001/api/ride-requests/search?${queryParams}`);
      const data = await response.json();
      setSearchResults(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error searching ride requests:', error);
      alert(`An error occurred while searching: ${error.message}`);
    }
  };

  const fetchRideDetails = async (id) => {
    try {
      const response = await fetch(`http://localhost:3001/api/ride-requests/${id}`);
      const data = await response.json();
      setSelectedRide(data);
      setIsModalOpen(true);
    } catch (error) {
      console.error('Error fetching ride details:', error);
      alert(`An error occurred while fetching ride details: ${error.message}`);
    }
  };
  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white font-sans">
        <Header />
        <main className="container mx-auto px-4 pt-24">
          <Switch>
            <Route exact path="/">
              <h1 className="text-6xl font-bold mb-6">Welcome to IIT Guwahati Ride Share</h1>
              <p className="text-xl mb-8">Find your perfect ride with ease.</p>
              <RideForm onSubmit={handleSubmit} />
              <div className="mt-12">
                <h2 className="text-2xl font-semibold mb-4">Recent Ride Requests</h2>
                <RideList rides={fetchRideDetails} onRideClick={handleSearch} />
              </div>
            </Route>
            {/* Add more routes for About, Features, Contact pages */}
          </Switch>
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
