import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import RideForm from './components/RideForm';
import RideList from './components/RideList';
import './App.css';

const App = () => {
  const [recentRides, setRecentRides] = useState([]);

  useEffect(() => {
    fetchRecentRequests();
  }, []);

  const fetchRecentRequests = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests');
      const data = await response.json();
      setRecentRequests(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching recent requests:', error);
      setRecentRequests([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, phone, destination, date, notes }),
      });
      if (response.ok) {
        alert('Ride request submitted successfully!');
        setName('');
        setPhone('');
        setDestination('airport');
        setDate('');
        setNotes('');
        fetchRecentRequests();
      } else {
        const errorData = await response.json();
        alert(`Failed to submit ride request: ${errorData.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error submitting ride request:', error);
      alert(`An error occurred: ${error.message}`);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const queryParams = new URLSearchParams({
        destination: searchDestination,
        date: searchDate,
      }).toString();
      const response = await fetch(`http://localhost:3001/api/ride-requests/search?${queryParams}`);
      const data = await response.json();
      setSearchResults(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error searching ride requests:', error);
      alert(`An error occurred while searching: ${error.message}`);
    }
  };

  const fetchRideDetails = async (id) => {
    try {
      const response = await fetch(`http://localhost:3001/api/ride-requests/${id}`);
      const data = await response.json();
      setSelectedRide(data);
      setIsModalOpen(true);
    } catch (error) {
      console.error('Error fetching ride details:', error);
      alert(`An error occurred while fetching ride details: ${error.message}`);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-purple-700 text-white font-sans">
      <header className="bg-white bg-opacity-10 backdrop-blur-md">
        <div className="container mx-auto px-4 py-6">
          <h1 className="text-4xl md:text-5xl font-bold text-center">
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-yellow-400 to-pink-500">
              IIT Guwahati Ride Share
            </span>
          </h1>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="bg-white bg-opacity-10 backdrop-blur-md rounded-xl shadow-2xl overflow-hidden">
          <div className="flex border-b border-white border-opacity-20">
            <button
              className={`flex-1 py-4 text-center font-semibold transition duration-300 ${
                activeTab === 'submit'
                  ? 'bg-white bg-opacity-20 text-white'
                  : 'text-white text-opacity-60 hover:bg-white hover:bg-opacity-10'
              }`}
              onClick={() => setActiveTab('submit')}
            >
              Submit a Ride Request
            </button>
            <button
              className={`flex-1 py-4 text-center font-semibold transition duration-300 ${
                activeTab === 'search'
                  ? 'bg-white bg-opacity-20 text-white'
                  : 'text-white text-opacity-60 hover:bg-white hover:bg-opacity-10'
              }`}
              onClick={() => setActiveTab('search')}
            >
              Search Ride Requests
            </button>
          </div>

          <div className="p-6">
            {activeTab === 'submit' ? (
              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="flex flex-col md:flex-row md:space-x-4">
                  <div className="flex-1 space-y-4">
                    <div className="relative">
                      <FiUser className="absolute top-3 left-3 text-white text-opacity-60" />
                      <input
                        className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                        placeholder="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                      />
                    </div>
                    <div className="relative">
                      <FiPhone className="absolute top-3 left-3 text-white text-opacity-60" />
                      <input
                        className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                        placeholder="Phone"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                      />
                    </div>
                  </div>
                  <div className="flex-1 space-y-4">
                    <div className="relative">
                      <FiMapPin className="absolute top-3 left-3 text-white text-opacity-60" />
                      <select
                        className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                        value={destination}
                        onChange={(e) => setDestination(e.target.value)}
                        required
                      >
                        <option value="airport">Airport</option>
                        <option value="railway station">Railway Station</option>
                      </select>
                    </div>
                    <div className="relative">
                      <FiCalendar className="absolute top-3 left-3 text-white text-opacity-60" />
                      <input
                        className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                        type="datetime-local"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        required
                      />
                    </div>
                  </div>
                </div>
                <div className="relative">
                  <FiInfo className="absolute top-3 left-3 text-white text-opacity-60" />
                  <textarea
                    className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                    placeholder="Notes"
                    value={notes}
                    onChange={(e) => setNotes(e.target.value)}
                    rows="3"
                  />
                </div>
                <button
                  className="w-full px-4 py-2 bg-gradient-to-r from-yellow-400 to-pink-500 text-white rounded-lg hover:from-yellow-500 hover:to-pink-600 transition duration-300 flex items-center justify-center font-semibold"
                  type="submit"
                >
                  <FiPlus className="mr-2" /> Submit Request
                </button>
              </form>
            ) : (
              <div>
                <form onSubmit={handleSearch} className="space-y-4 mb-6">
                  <div className="flex flex-col md:flex-row md:space-x-4">
                    <div className="flex-1 relative">
                      <FiMapPin className="absolute top-3 left-3 text-white text-opacity-60" />
                      <select
                        className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                        value={searchDestination}
                        onChange={(e) => setSearchDestination(e.target.value)}
                      >
                        <option value="">Select Destination</option>
                        <option value="airport">Airport</option>
                        <option value="railway station">Railway Station</option>
                      </select>
                    </div>
                    <div className="flex-1 relative">
                      <FiCalendar className="absolute top-3 left-3 text-white text-opacity-60" />
                      <input
                        className="w-full p-2 pl-10 bg-white bg-opacity-10 rounded-lg focus:outline-none focus:ring-2 focus:ring-yellow-400 transition duration-300"
                        type="date"
                        value={searchDate}
                        onChange={(e) => setSearchDate(e.target.value)}
                      />
                    </div>
                  </div>
                  <button
                    className="w-full px-4 py-2 bg-gradient-to-r from-yellow-400 to-pink-500 text-white rounded-lg hover:from-yellow-500 hover:to-pink-600 transition duration-300 flex items-center justify-center font-semibold"
                    type="submit"
                  >
                    <FiSearch className="mr-2" /> Search Rides
                  </button>
                </form>

                <div className="space-y-4">
                  <h3 className="text-xl font-semibold mb-2">Search Results</h3>
                  {searchResults.length > 0 ? (
                    <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                      {searchResults.map((request) => (
                        <div
                          key={request.id}
                          className="bg-white bg-opacity-10 p-4 rounded-lg hover:bg-opacity-20 transition duration-300 cursor-pointer transform hover:scale-105"
                          onClick={() => fetchRideDetails(request.id)}
                        >
                          <p className="font-semibold">{request.name}</p>
                          <p className="text-sm flex items-center">
                            <FiMapPin className="mr-1" /> {request.destination}
                          </p>
                          <p className="text-sm flex items-center">
                            <FiCalendar className="mr-1" /> {new Date(request.date).toLocaleString()}
                          </p>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p>No matching ride requests found.</p>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="mt-12 bg-white bg-opacity-10 backdrop-blur-md rounded-xl shadow-2xl p-6">
          <h2 className="text-2xl font-semibold mb-4">Recent Ride Requests</h2>
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {recentRequests.map((request) => (
              <div
                key={request.id}
                className="bg-white bg-opacity-10 p-4 rounded-lg hover:bg-opacity-20 transition duration-300 cursor-pointer transform hover:scale-105"
                onClick={() => fetchRideDetails(request.id)}
              >
                <p className="font-semibold">{request.name}</p>
                <p className="text-sm flex items-center">
                  <FiMapPin className="mr-1" /> {request.destination}
                </p>
                <p className="text-sm flex items-center">
                  <FiCalendar className="mr-1" /> {new Date(request.date).toLocaleString()}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="mt-12 bg-white bg-opacity-10 backdrop-blur-md rounded-xl shadow-2xl p-6">
          <h2 className="text-2xl font-semibold mb-4">Travel Tips</h2>
          <ul className="space-y-2">
            <li className="flex items-start">
              <span className="bg-yellow-400 rounded-full p-1 mr-3 mt-1">
                <FiInfo className="text-purple-700" />
              </span>
              Always confirm your ride details before leaving.
            </li>
            <li className="flex items-start">
              <span className="bg-yellow-400 rounded-full p-1 mr-3 mt-1">
                <FiUser className="text-purple-700" />
              </span>
              Share your ride status with a friend or family member.
            </li>
            <li className="flex items-start">
              <span className="bg-yellow-400 rounded-full p-1 mr-3 mt-1">
                <FiClock className="text-purple-700" />
              </span>
              Be punctual to respect other riders' time.
            </li>
            <li className="flex items-start">
              <span className="bg-yellow-400 rounded-full p-1 mr-3 mt-1">
                <FiPhone className="text-purple-700" />
              </span>
              Keep the contact information of your co-riders handy.
            </li>
          </ul>
        </div>
      </main>

      {isModalOpen && selectedRide && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white text-gray-800 rounded-lg p-6 max-w-md w-full">
            <h3 className="text-2xl font-bold mb-4">Ride Details</h3>
            <p><strong>Name:</strong> {selectedRide.name}</p>
            <p><strong>Phone:</strong> {selectedRide.phone}</p>
            <p><strong>Destination:</strong> {selectedRide.destination}</p>
            <p><strong>Date:</strong> {new Date(selectedRide.date).toLocaleString()}</p>
            <p><strong>Notes:</strong> {selectedRide.notes}</p>
            <button
              className="mt-4 px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition duration-300"
              onClick={() => setIsModalOpen(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      <footer className="bg-white bg-opacity-10 backdrop-blur-md py-4 mt-12">
        <div className="container mx-auto px-4 text-center">
          <p>&copy; 2024 IIT Guwahati Ride Share. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default RideShareApp;
