import React, { useState, useEffect } from 'react';

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
    fontFamily: 'Arial, sans-serif',
    padding: '20px',
  },
  header: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: '2rem',
    padding: '1rem',
    background: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '10px',
  },
  section: {
    background: 'rgba(255, 255, 255, 0.2)',
    borderRadius: '10px',
    padding: '20px',
    marginBottom: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  input: {
    padding: '10px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    color: 'white',
  },
  button: {
    padding: '10px',
    borderRadius: '5px',
    border: 'none',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
    fontWeight: 'bold',
  },
  list: {
    listStyleType: 'none',
    padding: 0,
  },
  listItem: {
    marginBottom: '10px',
    padding: '10px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '5px',
  },
};

const RideShareHomepage = () => {
  const [recentRequests, setRecentRequests] = useState([]);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [destination, setDestination] = useState('airport');
  const [date, setDate] = useState('');
  const [notes, setNotes] = useState('');
  const [searchDestination, setSearchDestination] = useState('');
  const [searchDate, setSearchDate] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    fetchRecentRequests();
  }, []);

  const fetchRecentRequests = async () => {
    // Simulating API call
    setRecentRequests([
      { id: 1, name: 'John Doe', destination: 'Airport', date: '2024-07-04' },
      { id: 2, name: 'Jane Smith', destination: 'Railway Station', date: '2024-07-05' },
    ]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Ride request submitted!');
    // Add logic to submit the request
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Add logic to search ride requests
    setSearchResults([
      { id: 3, name: 'Alice Johnson', destination: 'Airport', date: '2024-07-06' },
    ]);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>IIT Guwahati Ride Share</h1>

      <div style={styles.section}>
        <h2>Submit a Ride Request</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <input style={styles.input} type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
          <input style={styles.input} type="tel" placeholder="Phone" value={phone} onChange={(e) => setPhone(e.target.value)} required />
          <select style={styles.input} value={destination} onChange={(e) => setDestination(e.target.value)} required>
            <option value="airport">Airport</option>
            <option value="railway station">Railway Station</option>
          </select>
          <input style={styles.input} type="datetime-local" value={date} onChange={(e) => setDate(e.target.value)} required />
          <textarea style={styles.input} placeholder="Notes" value={notes} onChange={(e) => setNotes(e.target.value)}></textarea>
          <button style={styles.button} type="submit">Submit Request</button>
        </form>
      </div>

      <div style={styles.section}>
        <h2>Search Ride Requests</h2>
        <form onSubmit={handleSearch} style={styles.form}>
          <select style={styles.input} value={searchDestination} onChange={(e) => setSearchDestination(e.target.value)}>
            <option value="">Select Destination</option>
            <option value="airport">Airport</option>
            <option value="railway station">Railway Station</option>
          </select>
          <input style={styles.input} type="date" value={searchDate} onChange={(e) => setSearchDate(e.target.value)} />
          <button style={styles.button} type="submit">Search Rides</button>
        </form>
        <div>
          <h3>Search Results</h3>
          {searchResults.length > 0 ? (
            <ul style={styles.list}>
              {searchResults.map(request => (
                <li key={request.id} style={styles.listItem}>
                  {request.name} - {request.destination} - {request.date}
                </li>
              ))}
            </ul>
          ) : (
            <p>No matching ride requests found.</p>
          )}
        </div>
      </div>

      <div style={styles.section}>
        <h2>Recent Ride Requests</h2>
        <ul style={styles.list}>
          {recentRequests.map(request => (
            <li key={request.id} style={styles.listItem}>
              {request.name} - {request.destination} - {request.date}
            </li>
          ))}
        </ul>
      </div>

      <div style={styles.section}>
        <h2>Travel Tips</h2>
        <ul style={styles.list}>
          <li style={styles.listItem}>Always confirm your ride details before leaving.</li>
          <li style={styles.listItem}>Share your ride status with a friend or family member.</li>
          <li style={styles.listItem}>Be punctual to respect other riders' time.</li>
          <li style={styles.listItem}>Keep the contact information of your co-riders handy.</li>
        </ul>
      </div>
    </div>
  );
};

export default RideShareHomepage;
