import React, { useState, useEffect } from 'react';
import { FiCalendar, FiMapPin, FiPhone, FiInfo, FiSearch, FiPlus, FiX } from 'react-icons/fi';

const RideShareHomepage = () => {
  const [recentRequests, setRecentRequests] = useState([]);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [destination, setDestination] = useState('airport');
  const [date, setDate] = useState('');
  const [notes, setNotes] = useState('');
  const [searchDestination, setSearchDestination] = useState('');
  const [searchDate, setSearchDate] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedRide, setSelectedRide] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetchRecentRequests();
  }, []);

  const fetchRecentRequests = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests');
      const data = await response.json();
      setRecentRequests(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching recent requests:', error);
      setRecentRequests([]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, phone, destination, date, notes }),
      });
      if (response.ok) {
        alert('Ride request submitted successfully!');
        setName('');
        setPhone('');
        setDestination('airport');
        setDate('');
        setNotes('');
        fetchRecentRequests();
      } else {
        const errorData = await response.json();
        alert(`Failed to submit ride request: ${errorData.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error submitting ride request:', error);
      alert(`An error occurred: ${error.message}`);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const queryParams = new URLSearchParams({
        destination: searchDestination,
        date: searchDate,
      }).toString();
      const response = await fetch(`http://localhost:3001/api/ride-requests/search?${queryParams}`);
      const data = await response.json();
      setSearchResults(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error searching ride requests:', error);
      alert(`An error occurred while searching: ${error.message}`);
    }
  };

  const fetchRideDetails = async (id) => {
    try {
      const response = await fetch(`http://localhost:3001/api/ride-requests/${id}`);
      const data = await response.json();
      setSelectedRide(data);
      setIsModalOpen(true);
    } catch (error) {
      console.error('Error fetching ride details:', error);
      alert(`An error occurred while fetching ride details: ${error.message}`);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-500 text-white font-sans">
      <div className="container mx-auto p-4 flex flex-col items-center">
        <h1 className="text-5xl font-bold mb-12 text-center py-8 bg-white bg-opacity-10 rounded-full px-8 shadow-lg">
          IIT Guwahati Ride Share
        </h1>

        <div className="w-full max-w-4xl grid md:grid-cols-2 gap-8">
          <div className="bg-white bg-opacity-10 p-8 rounded-2xl shadow-lg backdrop-blur-md transform hover:scale-105 transition duration-300">
            <h2 className="text-3xl font-semibold mb-6 text-center">Submit a Ride Request</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                placeholder="Name" 
                value={name} 
                onChange={(e) => setName(e.target.value)}
                required
              />
              <input 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                placeholder="Phone" 
                value={phone} 
                onChange={(e) => setPhone(e.target.value)}
                required
              />
              <select 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                value={destination} 
                onChange={(e) => setDestination(e.target.value)}
                required
              >
                <option value="airport">Airport</option>
                <option value="railway station">Railway Station</option>
              </select>
              <input 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                type="datetime-local" 
                value={date} 
                onChange={(e) => setDate(e.target.value)}
                required
              />
              <textarea 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                placeholder="Notes" 
                value={notes} 
                onChange={(e) => setNotes(e.target.value)}
              />
              <button 
                className="w-full px-4 py-3 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition duration-300 flex items-center justify-center text-lg font-semibold"
                type="submit"
              >
                <FiPlus className="mr-2" /> Submit Request
              </button>
            </form>
          </div>

          <div className="bg-white bg-opacity-10 p-8 rounded-2xl shadow-lg backdrop-blur-md transform hover:scale-105 transition duration-300">
            <h2 className="text-3xl font-semibold mb-6 text-center">Search Ride Requests</h2>
            <form onSubmit={handleSearch} className="space-y-4">
              <select 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                value={searchDestination} 
                onChange={(e) => setSearchDestination(e.target.value)}
              >
                <option value="">Select Destination</option>
                <option value="airport">Airport</option>
                <option value="railway station">Railway Station</option>
              </select>
              <input 
                className="w-full p-3 bg-white bg-opacity-20 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-300 transition duration-300"
                type="date" 
                value={searchDate} 
                onChange={(e) => setSearchDate(e.target.value)}
              />
              <button 
                className="w-full px-4 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300 flex items-center justify-center text-lg font-semibold"
                type="submit"
              >
                <FiSearch className="mr-2" /> Search Rides
              </button>
            </form>

            <div className="mt-8">
              <h3 className="text-2xl font-semibold mb-4 text-center">Search Results</h3>
              {searchResults.length > 0 ? (
                <div className="space-y-3">
                  {searchResults.map(request => (
                    <div key={request.id} className="bg-white bg-opacity-20 p-4 rounded-lg hover:bg-opacity-30 transition duration-300 cursor-pointer" onClick={() => fetchRideDetails(request.id)}>
                      <p className="font-semibold">{request.name}</p>
                      <p className="text-sm flex items-center">
                        <FiMapPin className="mr-1" /> {request.destination}
                      </p>
                      <p className="text-sm flex items-center">
                        <FiCalendar className="mr-1" /> {new Date(request.date).toLocaleString()}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-center italic">No matching ride requests found.</p>
              )}
            </div>
          </div>
        </div>

        <div className="w-full max-w-4xl mt-12 bg-white bg-opacity-10 p-8 rounded-2xl shadow-lg backdrop-blur-md">
          <h2 className="text-3xl font-semibold mb-6 text-center">Recent Ride Requests</h2>
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {recentRequests.map(request => (
              <div key={request.id} className="bg-white bg-opacity-20 p-4 rounded-lg hover:bg-opacity-30 transition duration-300 cursor-pointer transform hover:scale-105" onClick={() => fetchRideDetails(request.id)}>
                <p className="font-semibold">{request.name}</p>
                <p className="text-sm flex items-center">
                  <FiMapPin className="mr-1" /> {request.destination}
                </p>
                <p className="text-sm flex items-center">
                  <FiCalendar className="mr-1" /> {new Date(request.date).toLocaleString()}
                </p>
              </div>
            ))}
          </div>
        </div>

        <div className="w-full max-w-4xl mt-12 bg-white bg-opacity-10 p-8 rounded-2xl shadow-lg backdrop-blur-md">
          <h2 className="text-3xl font-semibold mb-6 text-center">Travel Tips</h2>
          <ul className="list-disc pl-5 space-y-2 text-lg">
            <li>Always confirm your ride details before leaving.</li>
            <li>Share your ride status with a friend or family member.</li>
            <li>Be punctual to respect other riders' time.</li>
            <li>Keep the contact information of your co-riders handy.</li>
          </ul>
        </div>
      </div>

      {isModalOpen && selectedRide && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white text-gray-800 rounded-lg p-8 max-w-md w-full">
            <div className="flex justify-between items-center mb-6">
              <h3 className="text-2xl font-bold">{selectedRide.name}'s Ride Details</h3>
              <button onClick={() => setIsModalOpen(false)} className="text-gray-500 hover:text-gray-700 transition duration-300">
                <FiX size={24} />
              </button>
            </div>
            <div className="space-y-4">
              <p className="flex items-center text-lg">
                <FiMapPin className="mr-2 text-purple-500" /> <span className="font-semibold">Destination:</span> {selectedRide.destination}
              </p>
              <p className="flex items-center text-lg">
                <FiCalendar className="mr-2 text-purple-500" /> <span className="font-semibold">Date:</span> {new Date(selectedRide.date).toLocaleString()}
              </p>
              <p className="flex items-center text-lg">
                <FiPhone className="mr-2 text-purple-500" /> <span className="font-semibold">Phone:</span> {selectedRide.phone}
              </p>
              <p className="flex items-center text-lg">
                <FiInfo className="mr-2 text-purple-500" /> <span className="font-semibold">Notes:</span> {selectedRide.notes || 'No additional notes'}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RideShareHomepage;
