// src/components/RideList.js

import React from 'react';
import { FiMapPin, FiCalendar } from 'react-icons/fi';

const RideList = ({ rides, onRideClick }) => {
  return (
    <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
      {rides.map((ride) => (
        <div
          key={ride.id}
          className="bg-white bg-opacity-10 p-4 rounded-lg hover:bg-opacity-20 transition duration-300 cursor-pointer transform hover:scale-105"
          onClick={() => onRideClick(ride.id)}
        >
          <p className="font-semibold">{ride.name}</p>
          <p className="text-sm flex items-center">
            <FiMapPin className="mr-1" /> {ride.destination}
          </p>
          <p className="text-sm flex items-center">
            <FiCalendar className="mr-1" /> {new Date(ride.date).toLocaleString()}
          </p>
        </div>
      ))}
    </div>
  );
};

export default RideList;
