// src/components/RideForm.js

import React, { useState } from 'react';
import { FiUser, FiPhone, FiMapPin, FiCalendar, FiInfo } from 'react-icons/fi';

const RideForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    destination: 'airport',
    date: '',
    notes: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4 bg-white bg-opacity-10 p-6 rounded-lg">
      <div className="relative">
        <FiUser className="absolute top-3 left-3 text-gray-400" />
        <input
          className="w-full p-2 pl-10 bg-transparent border border-gray-600 rounded-lg focus:outline-none focus:border-purple-500 transition duration-300"
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>
      {/* Add similar styled inputs for phone, destination, date, and notes */}
      <button
        type="submit"
        className="w-full px-4 py-2 bg-gradient-to-r from-purple-500 to-indigo-600 text-white rounded-lg hover:from-purple-600 hover:to-indigo-700 transition duration-300"
      >
        Submit Request
      </button>
    </form>
  );
};

export default RideForm;
