import React, { useState, useEffect } from 'react';

const RideShareHomepage = () => {
  const [recentRequests, setRecentRequests] = useState([]);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [destination, setDestination] = useState('');
  const [date, setDate] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    fetchRecentRequests();
  }, []);

  const fetchRecentRequests = async () => {
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests');
      const data = await response.json();
      // Ensure that data is an array
      setRecentRequests(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching recent requests:', error);
      setRecentRequests([]); // Set to empty array in case of error
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/api/ride-requests', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, phone, destination, date, notes }),
      });
      if (response.ok) {
        alert('Ride request submitted successfully!');
        setName('');
        setPhone('');
        setDestination('');
        setDate('');
        setNotes('');
        fetchRecentRequests();
      } else {
        const errorData = await response.json();
        alert(`Failed to submit ride request: ${errorData.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error submitting ride request:', error);
      alert('An error occurred. Please try again.');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">IIT Guwahati Ride Share</h1>

      <div className="mb-6 p-4 bg-white shadow rounded">
        <h2 className="text-xl font-semibold mb-2">Submit a Ride Request</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input 
            className="w-full p-2 border rounded"
            placeholder="Name" 
            value={name} 
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input 
            className="w-full p-2 border rounded"
            placeholder="Phone" 
            value={phone} 
            onChange={(e) => setPhone(e.target.value)}
            required
          />
          <input 
            className="w-full p-2 border rounded"
            placeholder="Destination" 
            value={destination} 
            onChange={(e) => setDestination(e.target.value)}
            required
          />
          <input 
            className="w-full p-2 border rounded"
            type="datetime-local" 
            value={date} 
            onChange={(e) => setDate(e.target.value)}
            required
          />
          <textarea 
            className="w-full p-2 border rounded"
            placeholder="Notes" 
            value={notes} 
            onChange={(e) => setNotes(e.target.value)}
          />
          <button 
            className="w-full px-4 py-2 bg-blue-500 text-white rounded"
            type="submit"
          >
            Submit Request
          </button>
        </form>
      </div>

      <div className="mb-6 p-4 bg-white shadow rounded">
        <h2 className="text-xl font-semibold mb-2">Recent Ride Requests</h2>
        {recentRequests.length > 0 ? (
          recentRequests.map(request => (
            <div key={request.id} className="mb-2 p-2 border rounded">
              <p>{request.name} is traveling to {request.destination} on {new Date(request.date).toLocaleString()}</p>
            </div>
          ))
        ) : (
          <p>No recent ride requests available.</p>
        )}
      </div>

      <div className="p-4 bg-white shadow rounded">
        <h2 className="text-xl font-semibold mb-2">Travel Tips</h2>
        <ul className="list-disc pl-5">
          <li>Always verify the identity of your ride-share partner</li>
          <li>Share your travel plans with a friend or family member</li>
          <li>Meet in a public place when connecting with your ride-share</li>
          <li>Don't forget to rate your experience after the ride</li>
        </ul>
      </div>

      <div className="mt-6 text-center text-sm text-gray-500">
        Next holiday: Summer Break (July 15 - August 15, 2024)
      </div>
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <RideShareHomepage />
    </div>
  );
}

export default App;
